import React, { useState } from 'react';
import './FAQ.css';

export default function FAQ() {
  const [activeIndex, setActiveIndex] = useState(null);

  const toggleFAQ = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  const faqs = [
    {
      question: 'What is Gen AI Editor?',
      answer: 'Gen AI Editor is a smart, AI-powered platform built by IIT Indore students to simplify video and photo editing using cutting-edge tools.'
    },
    {
      question: 'Is it free to use?',
      answer: 'Yes, the basic tools are free to use. Advanced features may require login or limited access depending on future updates.'
    },
    {
      question: 'What types of editing can I do?',
      answer: 'You can remove backgrounds from full videos, eliminate objects from image frames, enhance visuals, apply filters, and more.'
    },
    {
      question: 'Do I need any experience to use this?',
      answer: 'No! Gen AI is designed for everyone — from beginners to professionals. It’s simple, intuitive, and fast.'
    },
    {
      question: 'Can I upload and edit long videos?',
      answer: 'Yes. Our backend supports processing full-length videos, although performance may depend on device specs and internet speed.'
    },
    {
      question: 'Which AI models power this app?',
      answer: 'We use Robust Video Matting (RVM), Mask2Former, LaMa, and other state-of-the-art models for accurate and fast editing.'
    },
    {
      question: 'Do I need to install anything?',
      answer: 'No installation is required. The platform is fully browser-based — just visit the site and start editing.'
    },
    {
      question: 'Is my data safe?',
      answer: 'Yes. Your files are temporarily processed and deleted after editing. We don’t store user data.'
    },
    {
      question: 'Will there be more tools in the future?',
      answer: 'Absolutely! We’re working on smart color grading, AI upscaling, motion tracking, and even style transfer tools.'
    },
    {
      question: 'Can I suggest a feature?',
      answer: 'Yes, we welcome suggestions. Just head over to the Contact page and let us know what you\'d like to see.'
    },
    {
      question: 'How fast is the processing?',
      answer: 'Most tasks complete within seconds to a few minutes depending on video size and processing complexity.'
    },
    {
      question: 'Can I collaborate or contribute?',
      answer: 'Yes, we love collaborations! Whether you\'re a developer, designer, or just a curious learner, feel free to contact us.'
    },
    {
      question: 'Is this app mobile-friendly?',
      answer: 'The platform works on mobile browsers too, but for best results, we recommend using a desktop/laptop.'
    },
    {
      question: 'Who built this platform?',
      answer: 'Gen AI Editor is built by a passionate team of students from IIT Indore, driven to democratize content creation through AI.'
    },
  ];

  return (
    <section className="faq-section">
      <div className="faq-header">
        <h1>Frequently Asked Questions</h1>
        <p>Here are answers to some common questions about Gen AI Editor.</p>
      </div>

      <div className="faq-list">
        {faqs.map((faq, index) => (
          <div className={`faq-item ${activeIndex === index ? 'active' : ''}`} key={index}>
            <div className="faq-question" onClick={() => toggleFAQ(index)}>
              <h3>{faq.question}</h3>
            </div>
            {activeIndex === index && (
              <div className="faq-answer">
                <p>{faq.answer}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </section>
  );
}
