import React from 'react';
import './Contact.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faEnvelope,
  faPhone,
} from '@fortawesome/free-solid-svg-icons';
import {
  faGithub,
  faLinkedinIn,
  faInstagram,
  faXTwitter,
  faDiscord,
  faYoutube,
} from '@fortawesome/free-brands-svg-icons';

const team = [
  {
    name: "Saharsh",
    role: "Team Leader",
    email: "mailto:ee240002063@iiti.ac.in",
    github: "",
    linkedin: "",
  },
  {
    name: "Sreyan",
    role: "Backend Developer",
    email: "mailto:ep240051018@iiti.ac.in",
    github: "",
    linkedin: "",
  },
  {
    name: "Abhishek",
    role: "AI Model Integration",
    email: "mailto:mems240005038@iiti.ac.in",
    github: "",
    linkedin: "",
  },
  {
    name: "Vikram",
    role: "Frontend Developer",
    email: "mailto:cse240001078@iiti.ac.in",
    github: "",
    linkedin: "",
  },
  {
    name: "Pankaj",
    role: "UI/UX + System Design",
    email: "mailto:ee240002048@iiti.ac.in",
    github: "",
    linkedin: "",
  }
];

export default function Contact() {
  return (
    <section className="contact-broad">
      <div className="contact-head">
        <h1>Contact Us</h1>
        <p>We are a group of students from IIT Indore passionate about building smart AI tools for photo & video editing.</p>
      </div>

      <div className="team-section">
        {team.map((member, idx) => (
          <div className="team-card" key={idx}>
            <h3>{member.name}</h3>
            <p>{member.role}</p>
            <div className="icons">
              <a href={member.email}><FontAwesomeIcon icon={faEnvelope} /></a>
              <a href={member.linkedin}><FontAwesomeIcon icon={faLinkedinIn} /></a>
              <a href={member.github}><FontAwesomeIcon icon={faGithub} /></a>
            </div>
          </div>
        ))}
      </div>

      <div className="general-links">
        <h2>Connect With Us</h2>
        <div className="social-icons">
          <a href=""><FontAwesomeIcon icon={faInstagram} /></a>
          <a href=""><FontAwesomeIcon icon={faXTwitter} /></a>
          <a href=""><FontAwesomeIcon icon={faDiscord} /></a>
          <a href=""><FontAwesomeIcon icon={faYoutube} /></a>
          <a href=""><FontAwesomeIcon icon={faPhone} /></a>
        </div>
      </div>

      <div className="contact-footer-note">
        <p>We're always open to collaborations, feedback, or just a friendly chat. Reach out anytime.</p>
        <p><em>“We don’t just build tools — we build the future of creativity.”</em></p>
      </div>
    </section>
  );
}
