import React, { useEffect } from 'react';
import './About.css';

export default function About() {
  // ✨ Scroll Reveal + Cursor Light
  useEffect(() => {
    // Scroll-triggered animation
    const elements = document.querySelectorAll('[data-animate]');
    const observer = new IntersectionObserver(entries => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('visible');
          observer.unobserve(entry.target);
        }
      });
    }, { threshold: 0.2 });

    elements.forEach(el => observer.observe(el));

    // Cursor glow effect
    const cursor = document.createElement('div');
    cursor.classList.add('cursor-light');
    document.body.appendChild(cursor);

    const move = (e) => {
      cursor.style.transform = `translate(${e.clientX}px, ${e.clientY}px)`;
    };

    window.addEventListener('mousemove', move);
    return () => {
      window.removeEventListener('mousemove', move);
      document.body.removeChild(cursor);
    };
  }, []);

  return (
    <section className="about-fullpage">
      <div className="about-overlay">
        <div className="about-inner">

          <h1 className="main-heading">About Us</h1>
          <p className="sub-heading">A project built with passion and teamwork 💻✨</p>

          <div className="about-section" data-animate>
            <p>
              We are a group of students from <strong>IIT Indore</strong> who came together to build something meaningful using the power of Artificial Intelligence. This project is part of our learning journey, where we explored real-world applications of AI in video and photo editing.
            </p>
            <p>
              Our goal was to create a simple and effective tool that allows users to easily remove backgrounds, erase unwanted objects, and enhance media using modern AI techniques.
            </p>
            <p>
              We collaborated as a team — dividing work between frontend, backend, model integration, and design — and supported each other throughout the development process.
            </p>
          </div>

          <div className="team" data-animate>
            <h2>👥 Our Team</h2>
            <ul>
              <li>💡 Ideation & Design: Team effort from scratch</li>
              <li>🧠 AI & Backend: Implemented using FastAPI and deep learning models</li>
              <li>🖥️ Frontend: Built with React and custom styling</li>
              <li>🤝 Collaboration: Smooth teamwork and shared responsibilities</li>
            </ul>
          </div>

          <div className="ending-quote" data-animate>
            <p><em>This is just the beginning. We’re excited to keep learning and building more cool stuff! 🚀</em></p>
          </div>

        </div>
      </div>
    </section>
  );
}
